angular.module("clocks").controller("projectCtrl",function(e,t,i,o){e.loadProject=function(){var i=t.id;o.getProject(i).then(function(t){t.time=o.revertFromSeconds(t.time_spent),t.time_budgeted&&(t.budgetDisplay=o.revertFromSeconds(t.time_budgeted)),e.viewProject=t})},e.loadProject(),e.startTimer=function(){e.running=!0,e.runningTimer=setInterval(function(){59==e.viewProject.time.seconds?(e.viewProject.time.seconds=0,59==e.viewProject.time.minutes?(e.viewProject.time.hours++,e.viewProject.time.minutes=0):e.viewProject.time.minutes++):e.viewProject.time.seconds++,e.viewProject.time_budgeted&&o.convertToSeconds(e.viewProject.time.hours,e.viewProject.time.minutes,e.viewProject.time.seconds)>e.viewProject.time_budgeted&&(e.viewProject.overdue=!0),e.$digest()},1e3)},e.stopTimer=function(){e.running=!1,clearInterval(e.runningTimer)},e.updateProject=function(){var t=o.convertToSeconds(e.viewProject.time.hours,e.viewProject.time.minutes,e.viewProject.time.seconds);o.updateProject(e.viewProject.id,t)}});